floor = 0

basement_search_index = 1
found_basement = false

input.each_char do |c|
  case c
  when '('
    floor += 1
  when ')'
    floor -= 1
  end
  if !found_basement && floor == -1
    found_basement = true
    puts "Entered basement at position #{basement_search_index}"
  else
    basement_search_index += 1
  end
end

